    public static String GetEmojifiedString(String original, EmojiState state){
        if(state.equals(EmojiState.NONE))
            return original;

        List<String> sentencePieces = Arrays.stream(original.split(" ")).toList();

        float percentForOneEmoji = 1 / (float)sentencePieces.size();
        float chanceToPlaceEmoji = percentForOneEmoji;

        List<String> formattedSentencePieces = new ArrayList<>(sentencePieces.size());

        for (String piece : sentencePieces)
        {
            float randomFloat = new Random().nextFloat();
            String currentPiece = piece;

            if (randomFloat <= chanceToPlaceEmoji)
            {
                currentPiece += state == EmojiState.CENSORED ? GetRandomCensoredEmoji() : GetRandomEmoji();
                chanceToPlaceEmoji -= percentForOneEmoji;
            }

            formattedSentencePieces.add(currentPiece);

            chanceToPlaceEmoji += percentForOneEmoji;
        }

        String resultString = String.join(" ", formattedSentencePieces);

        return resultString;
    }
